mapper.select:
SELECT * FROM TC_PRODUCT A, TC_P_FILE B
WHERE A.P_CODE = B.P_CODE
AND A.P_CODE = #{A.P_CODE}

mapper.update:
UPDATE TC_PRODUCT A,TC_P_FILE B SET
A.P_NAME = #{pName}
,A.PRICE = #{price}
,A.SALE_PRICE = #{salePrice}
,A.STOCK = #{stock}
,A.P_POINT = #{pPoint}
,A.P_DETAIL = #{pDetail}
,A.P_SMALL = #{pSmall}
,A.P_BIG = #{pBig}
,A.P_MIDDLE = #{pMiddle}
,A.P_SIZE =	#{pSize}
,A.P_MAIN = #{pMain}
,B.P_IMAGE_SRC = #{pImageSrc}
WHERE P_CODE = #{pCode}
